package practice;

public class ConvertInttoString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int i=10;
		
		String s =Integer.toString(i);
		
		System.out.println(i+20);// + performing binary operation
		
		System.out.println(s+20);// + performing concatenation operation

	}

}
